import React from "react";

function ProgressBar({ session, focusDuration, breaDuration }) {
  const focusDurationSeconds = focusDuration * 60;
  const secondsDivided = 1 - session?.timeRemaining / focusDurationSeconds;
  const secondsDividedPercentage = secondsDivided * 100;

  return (
    <>
      <div className="col">
        <div className="progress" style={{ height: "20px" }}>
          <div
            className="progress-bar"
            role="progressbar"
            aria-valuemin="0"
            aria-valuemax="100"
            aria-valuenow="0" // TODO: Increase aria-valuenow as elapsed time increases
            style={{
              width: secondsDividedPercentage,
            }} // TODO: Increase width % as elapsed time increases
          />
        </div>
      </div>
    </>
  );
}

export default ProgressBar;
